import React, { useEffect, useState } from 'react';

function Locations() {
  // Fetch Location Data
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    fetch('https://rickandmortyapi.com/api/location')
      .then(response => response.json())
      .then(data => setLocations(data.results))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // SVG Setup and Display Locations
  const svgWidth = window.innerWidth;
  const svgHeight = window.innerHeight;
  const maxInhabitants = Math.max(...locations.map(location => location.residents.length));

  return (
    <svg width={svgWidth} height={svgHeight}>
      {locations.map((location, index) => {
        const rectWidth = (location.residents.length / maxInhabitants) * svgWidth;
        const rectHeight = svgHeight / locations.length;
        const textPositionX = 10;  // 10 pixels from the left edge of the rectangle
        const textPositionY = index * rectHeight + 20;  // 20 pixels from the top edge of the rectangle

        return (
          <g key={index}>
            <rect
              x={0}
              y={index * rectHeight}
              width={rectWidth}
              height={rectHeight}
              fill="blue"
            />
            <text
              x={textPositionX}
              y={textPositionY}
              fill="pink"
              fontSize="12"
            >
              {location.name}: {location.residents.length} inhabitants
            </text>
          </g>
        );
      })}
    </svg>
  );
}

export default Locations;
