import React, { useEffect, useState } from 'react';
import Tippy from '@tippy.js/react';
import 'tippy.js/dist/tippy.css';

const Locations = () => {
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    fetch('https://rickandmortyapi.com/api/location')
      .then(response => response.json())
      .then(data => setLocations(data.results));
  }, []);

  const maxResidentsLength = Math.max(...locations.map(location => location.residents.length));
	const rectHeightPercentage = 100 / locations.length;

	console.log(locations.length);

  return (
    <svg width="100%" height="100vh">
      {locations.map((location, index) => {
        const rectWidthPercentage = (location.residents.length / maxResidentsLength) * 100;

        return (
          <g key={index}>
            <Tippy content={`${location.name}: ${location.residents.length} inhabitants`}>
              <rect
                x={0}
                y={`${index * rectHeightPercentage}%`} 
                width={`${rectWidthPercentage}%`} 
                height={`${rectHeightPercentage}%`}
                fill="lightblue"
              />
            </Tippy>

            <text
              x={0.5}
              y={`${(index * rectHeightPercentage) + rectHeightPercentage}%`}

            >
              {location.residents.length}
            </text>
          </g>
        );
      })}
    </svg>
  );
};

export default Locations;
