import React, { useEffect, useState } from 'react';

const Locations = () => {
	const [locations, setLocations] = useState([]);
	const [hoveredLocation, setHoveredLocation] = useState();

	useEffect(() => {
		fetch('https://rickandmortyapi.com/api/location')
			.then(response => response.json())
			.then(data => setLocations(data.results));
	}, []);

	const maxRectWidth = Math.max(...locations.map(location => location.residents.length));
	const rectHeightPercentage = 100 / locations.length;

	console.log(locations);

	const Popup = (location) => {
		return (
		<div className="popup">
			<h2>{location.name}</h2>
			<p>{location.residents}</p>
		</div>
		)
	}

	return (
		<div className="wrapper">
			<svg width="100%" height="100vh">
				{locations.map((location, index) => {
					const rectWidthPercentage = (location.residents.length / maxRectWidth) * 100;

					return (
						<g key={index}>
							<rect
								x={0}
								y={`${index * rectHeightPercentage}%`} 
								width="100%" 
								height={`${rectHeightPercentage}%`}
								fill="#f2f2f2"
								onMouseEnter = {
								  () => setHoveredLocation(location)
								}
								onMouseLeave = {
								  () => setHoveredLocation(null)
								}
							/>
							<rect
								x={0}
								y={`${index * rectHeightPercentage}%`} 
								width={`${rectWidthPercentage}%`} 
								height={`${rectHeightPercentage}%`}
								fill="lightblue"
							/>						
							<text
								x={0.5}
								y={`${(index * rectHeightPercentage) + rectHeightPercentage * 0.5}%`}

							>
								{location.residents.length}
							</text>
						</g>
					);
				})}
			</svg>
			<Popup location={hoveredLocation} />}
		</div>
	);
};

export default Locations;
