import React, { useEffect, useState } from 'react';

function Locations() {
  // Fetch Location Data
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    fetch('https://rickandmortyapi.com/api/location')
      .then(response => response.json())
      .then(data => setLocations(data.results))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // SVG Setup and Display Locations
  const svgWidth = window.innerWidth;
  const svgHeight = window.innerHeight;

  return (
    <svg width={svgWidth} height={svgHeight}>
      {locations.map((location, index) => {
        // Directly using the number of inhabitants to determine the rectangle width
        const rectWidth = location.residents.length;
        const rectHeight = svgHeight / locations.length;

        return (
          <rect
            key={index}
            x={0}
            y={index * rectHeight}
            width={rectWidth}
            height={rectHeight}
            fill="blue"
          >
            <title>{location.name}: {location.residents.length} inhabitants</title>
          </rect>
        );
      })}
    </svg>
  );
}

export default Locations;
