import React, { useEffect, useState } from 'react';

const Locations = () => {
	const [locations, setLocations] = useState([]);
	const [hoveredLocation, setHoveredLocation] = useState();

	useEffect(() => {
		fetch('https://rickandmortyapi.com/api/location')
			.then(response => response.json())
			.then(data => setLocations(data.results));
	}, []);

	const maxWidth = Math.max(...locations.map(location => location.residents.length));
	const height = 100 / locations.length;

	// console.log(locations);

	const Popup = ({ location: {name, residents, type} }) => {
		return (
			<div className="popup">
				<h2>{name}</h2>
				<p>Residents: {residents.length}</p>
				<p>Type: {type}</p>
			</div>
		)
	}

	return (
		<div className="wrapper">
			<svg width="100%" height="100vh">
				{locations.map((location, index) => {
					const width = (location.residents.length / maxWidth) * 100;

					return (
						<g key={location.id}>
							<rect
								x={0}
								y={`${index * height}%`} 
								width={`${width}%`} 
								height={`${height}%`}
								fill="lightblue"
								onMouseEnter = {
								  () => setHoveredLocation(location)
								}
								onMouseLeave = {
								  () => setHoveredLocation(null)
								}
							/>
							<text
								x={0.5}
								y={`${(index * height) + height * 0.5}%`}

							>
								{location.residents.length}
							</text>
						</g>
					);
				})}
			</svg>
			{hoveredLocation && <Popup location={hoveredLocation} />}
		</div>
	);
};

export default Locations;
