import React, { useEffect, useState } from 'react';
import Tippy from '@tippy.js/react';
import 'tippy.js/dist/tippy.css';

const Locations = () => {
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    fetch('https://rickandmortyapi.com/api/location')
      .then(response => response.json())
      .then(data => setLocations(data.results));
  }, []);

  const maxInhabitants = Math.max(...locations.map(location => location.residents.length));

  return (
    <svg width="100%" height="100%" viewBox="0 0 100 100" preserveAspectRatio="xMinYMin">
      {locations.map((location, index) => {
        const rectWidth = (location.residents.length / maxInhabitants) * 100;  // Width as a percentage
        const rectHeight = 100 / locations.length;  // Height as a percentage
        return (
          <g key={index}>
            <Tippy content={`${location.name}: ${location.residents.length} inhabitants`}>
              <rect
                x={0}
                y={index * rectHeight}
                width={`${rectWidth}%`}  // Width as a percentage
                height={`${rectHeight}%`}  // Height as a percentage
                fill="lightblue"
              />
            </Tippy>
            {/* Adding the text element to display the number of inhabitants */}
            <text
              x={0.5}  // Small padding from the left
              y={(index * rectHeight) + rectHeight * 0.15}  // Positioning text inside the rectangle
              fontSize="1"  // Adjust font size accordingly
            >
              {location.residents.length}
            </text>
          </g>
        );
      })}
    </svg>
  );
};

export default Locations;
