import React, { useEffect, useState } from 'react';
import ReactTooltip from 'react-tooltip';

const Locations = () => {
  const [locations, setLocations] = useState([]);
  const [svgWidth, setSvgWidth] = useState(window.innerWidth);
  const [svgHeight, setSvgHeight] = useState(window.innerHeight);

  useEffect(() => {
    fetch('https://rickandmortyapi.com/api/location')
      .then(response => response.json())
      .then(data => setLocations(data.results));
  }, []);

  useEffect(() => {
    const handleResize = () => {
      setSvgWidth(window.innerWidth);
      setSvgHeight(window.innerHeight);
    };
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const maxInhabitants = Math.max(...locations.map(location => location.residents.length));

  return (
    <svg width={svgWidth} height={svgHeight}>
      {locations.map((location, index) => {
        const rectWidth = (location.residents.length / maxInhabitants) * svgWidth;
        const rectHeight = svgHeight / locations.length;
        return (
          <g key={index}>
            <rect
              x={0}
              y={index * rectHeight}
              width={rectWidth}
              height={rectHeight}
              fill="blue"
              data-tip={`${location.name}: ${location.residents.length} inhabitants`}
            />
            <text x={10} y={index * rectHeight + 20} fill="white" fontSize="12">
              {location.name}: {location.residents.length} inhabitants
            </text>
          </g>
        );
      })}
      <ReactTooltip />
    </svg>
  );
};

export default Locations;
